    organic: { co2: 1.8, energy: 800, water: 0.4, landfill: 0.5, base_points: 6, rarity: 0.6 },
    food: { co2: 2.2, energy: 1000, water: 0.5, landfill: 0.6, base_points: 8, rarity: 0.7 },
