  // Smart categorization for unknown types
  const smartCategories = {
    // Electronic patterns
    electronic: ['device', 'gadget', 'screen', 'circuit', 'chip', 'cable', 'charger', 'adapter'],
    // Metal patterns
    metal: ['steel', 'iron', 'aluminum', 'copper', 'brass', 'tin', 'alloy', 'wire'],
    // Plastic patterns
    plastic: ['polymer', 'vinyl', 'acrylic', 'foam', 'synthetic', 'resin', 'bag', 'bottle'],
    // Organic patterns
    organic: ['bio', 'natural', 'plant', 'animal', 'biodegradable', 'compostable', 'leaf', 'branch'],
    // Textile patterns
    textile: ['fabric', 'cotton', 'wool', 'silk', 'polyester', 'leather', 'denim', 'linen'],
    // Hazardous patterns
    hazardous: ['toxic', 'chemical', 'poison', 'radioactive', 'flammable', 'corrosive', 'acid']
  };

  // Find matching category
  for (const [category, patterns] of Object.entries(smartCategories)) {
    if (patterns.some(pattern => type.includes(pattern))) {
      return knownFactors[category] || knownFactors.general;
    }
  }

  // Adaptive estimation for completely unknown types
  const estimatedWeight = contextualData.estimatedWeight || 1; // kg
  const materialComplexity = contextualData.materialComplexity || 'medium';

  const complexityMultipliers = {
    simple: 0.5,   // paper, cardboard
    medium: 1.0,   // most items
    complex: 2.0,  // electronics, composites
    hazardous: 3.0 // chemicals, medical
  };

  const multiplier = complexityMultipliers[materialComplexity] || 1.0;

  return {
    co2: 5.0 * multiplier * estimatedWeight,
    energy: 7000 * multiplier * estimatedWeight,
    water: 3.0 * multiplier * estimatedWeight,
    landfill: 1.0 * estimatedWeight,
    base_points: Math.round(15 * multiplier),
    rarity: 1.0 + (multiplier - 1.0) * 0.5
  };
}

// Adaptive disposal method multipliers
function getDisposalMultiplier(disposalMethod, wasteType, contextualData = {}) {
  const baseMultipliers = {
    // Preferred methods
    'prevention': 2.0,      // Best: didn't create waste
    'reuse': 1.8,          // Second best: direct reuse
    'upcycling': 1.7,      // Creative reuse
    'donation': 1.6,       // Giving to others
    'repair': 1.5,         // Fixing instead of disposing

    // Good methods
    'recycling': 1.0,      // Standard recycling
    'composting': 1.2,     // Organic processing
    'biogas': 1.3,         // Energy from organics

    // Acceptable methods
    'proper_disposal': 0.8, // Following guidelines
    'energy_recovery': 0.6, // Waste-to-energy
    'incineration': 0.4,   // Controlled burning

    // Last resort
    'landfill': 0.2        // Last resort
  };

  const method = disposalMethod.toLowerCase().replace(/[_\s-]/g, '_');
  let multiplier = baseMultipliers[method] || 1.0;

  // Adaptive adjustments based on waste type
  if (wasteType.includes('organic') && method === 'composting') {
    multiplier *= 1.3; // Bonus for organic composting
  }

  if (wasteType.includes('electronic') && method === 'recycling') {
    multiplier *= 1.2; // Bonus for e-waste recycling
  }

  if (wasteType.includes('hazardous') && method === 'proper_disposal') {
    multiplier *= 1.5; // Important for hazardous materials
  }

  // Context-based adjustments
  if (contextualData.facilityQuality === 'certified') {
    multiplier *= 1.1;
  }

  if (contextualData.distanceTraveled > 50) {
    multiplier *= 0.9; // Slight penalty for long transport
  }

  return multiplier;
}

// Adaptive user profiling and personalization
function getPersonalizationMultiplier(userProfile, experienceLevel, motivationLevel) {
  let multiplier = 1.0;

  // Experience level bonuses
  const experienceBonuses = {
    'beginner': 1.2,    // Encourage newcomers
    'intermediate': 1.0, // Standard
    'expert': 0.9,      // Slight reduction for experts
    'master': 0.8       // They don't need as much encouragement
  };

  multiplier *= experienceBonuses[experienceLevel] || 1.0;

  // Motivation level adjustments
  const motivationBonuses = {
    'low': 1.5,      // Extra encouragement needed
    'medium': 1.0,   // Standard
    'high': 0.8,     // Already motivated
    'extreme': 0.7   // Highly self-motivated
  };

  multiplier *= motivationBonuses[motivationLevel] || 1.0;

  // User goal alignment
  if (userProfile.goals) {
    const goalBonuses = {
      'environmental_impact': 1.1,
      'cost_savings': 1.0,
      'convenience': 0.9,
      'social_responsibility': 1.2,
      'regulatory_compliance': 0.8
    };

    userProfile.goals.forEach(goal => {
      if (goalBonuses[goal]) {
        multiplier *= goalBonuses[goal];
      }
    });
  }

  return multiplier;
}

