// EcoHaul Simple Adaptive Disposal Centers - Robust and Error-Free
const input = $json.body || $json;
const { 
  latitude, 
  longitude, 
  wasteType = 'general', 
  radius = 10, 
  maxResults = 10, 
  userPreferences = [], 
  urgency = 'normal',
  transportMethod = 'car',
  accessibility = 'normal',
  budget = 'any',
  timeConstraints = 'flexible',
  environmentalFocus = 'moderate'
} = input;

// Validate input
if (!latitude || !longitude) {
  return [{
    json: {
      success: false,
      error: 'Latitude and longitude are required',
      hint: 'Please provide latitude and longitude coordinates'
    }
  }];
}

// Simple waste type categorization
function categorizeWasteType(wasteType) {
  const categories = {
    plastic: { searchTerms: ['recycling center', 'plastic recycling'], priority: 'high' },
    metal: { searchTerms: ['scrap yard', 'metal recycling'], priority: 'high' },
    glass: { searchTerms: ['glass recycling', 'recycling center'], priority: 'medium' },
    paper: { searchTerms: ['recycling center', 'paper recycling'], priority: 'medium' },
    electronic: { searchTerms: ['electronic waste', 'e-waste center'], priority: 'high' },
    hazardous: { searchTerms: ['hazardous waste facility'], priority: 'critical' },
    organic: { searchTerms: ['composting facility', 'organic waste'], priority: 'medium' },
    food: { searchTerms: ['composting facility', 'food waste'], priority: 'medium' },
    textile: { searchTerms: ['textile recycling', 'clothing donation'], priority: 'medium' },
    pharmaceutical: { searchTerms: ['pharmacy take-back', 'drug disposal'], priority: 'high' },
    battery: { searchTerms: ['battery recycling', 'electronic waste'], priority: 'high' },
    paint: { searchTerms: ['paint disposal', 'hazardous waste'], priority: 'high' }
  };

  const type = wasteType.toLowerCase();
  return categories[type] || {
    searchTerms: [`${type} disposal`, 'waste management'],
    priority: 'medium'
  };
}

// Adaptive radius calculation
function calculateAdaptiveRadius(baseRadius, wasteInfo, urgency, environmentalFocus) {
  let adaptiveRadius = baseRadius;

  // Adjust for waste priority
  if (wasteInfo.priority === 'critical') adaptiveRadius *= 2.0;
  else if (wasteInfo.priority === 'high') adaptiveRadius *= 1.5;
  else if (wasteInfo.priority === 'low') adaptiveRadius *= 0.7;

  // Adjust for urgency
  if (urgency === 'urgent') adaptiveRadius *= 1.3;
  else if (urgency === 'flexible') adaptiveRadius *= 1.8;

  // Adjust for environmental focus
  if (environmentalFocus === 'high') adaptiveRadius *= 1.6;
  else if (environmentalFocus === 'low') adaptiveRadius *= 0.8;

  return Math.min(Math.max(adaptiveRadius, 2), 50); // Between 2km and 50km
}

// Transport constraints
function getTransportConstraints(transportMethod) {
  const constraints = {
    walking: { maxDistance: 2, preferences: ['nearby', 'pedestrian_friendly'] },
    bicycle: { maxDistance: 8, preferences: ['bike_friendly', 'secure_parking'] },
    car: { maxDistance: 30, preferences: ['parking_available', 'drive_through'] },
    public_transport: { maxDistance: 20, preferences: ['transit_accessible'] },
    wheelchair: { maxDistance: 5, preferences: ['wheelchair_accessible', 'ramp_access'] }
  };

  return constraints[transportMethod] || constraints.car;
}

// Budget constraints
function getBudgetConstraints(budget) {
  const constraints = {
    free: { maxCost: 0, label: 'Free only' },
    low: { maxCost: 20, label: 'Under $20' },
    moderate: { maxCost: 50, label: 'Under $50' },
    high: { maxCost: 200, label: 'Premium options' },
    any: { maxCost: 1000, label: 'Any cost' }
  };

  return constraints[budget] || constraints.any;
}

// Generate adaptive centers
function generateAdaptiveCenters(lat, lng, wasteInfo, constraints) {
  const centers = [
    {
      id: 'adaptive-center-1',
      name: `${wasteInfo.searchTerms[0].split(' ').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ')} Facility`,
      address: '123 Green Processing Way, EcoCity',
      latitude: lat + 0.008,
      longitude: lng + 0.012,
      distance: 1.2,
      rating: 4.6,
      phone: '(555) 123-GREEN',
      website: 'www.ecoprocessing.com',
      hours: 'Mon-Fri: 8AM-6PM, Sat: 9AM-4PM',
      acceptedWaste: [wasteType, 'general'],
      features: ['certified_facility', 'eco_friendly'],
      estimatedCost: wasteInfo.priority === 'critical' ? 25 : 0,
      priceRange: wasteInfo.priority === 'critical' ? '$25' : 'Free',
      source: 'adaptive'
    },
    {
      id: 'adaptive-center-2',
      name: 'Community Recycling Hub',
      address: '456 Sustainability Street',
      latitude: lat - 0.015,
      longitude: lng + 0.018,
      distance: 2.8,
      rating: 4.2,
      phone: '(555) 456-RECYCLE',
      website: 'www.communityrecycle.org',
      hours: 'Tue-Sun: 10AM-5PM',
      acceptedWaste: ['general', wasteType],
      features: ['community_operated', 'volunteer_assistance'],
      estimatedCost: 0,
      priceRange: 'Free',
      source: 'community'
    },
    {
      id: 'adaptive-center-3',
      name: 'Express Waste Solutions',
      address: '789 Quick Disposal Lane',
      latitude: lat + 0.022,
      longitude: lng - 0.009,
      distance: 3.5,
      rating: 4.8,
      phone: '(555) 789-EXPRESS',
      website: 'www.expresswaste.com',
      hours: '24/7 Drop-off Available',
      acceptedWaste: [wasteType, 'general', 'specialized'],
      features: ['24_hour_access', 'premium_service'],
      estimatedCost: constraints.budget.maxCost > 50 ? 45 : 15,
      priceRange: constraints.budget.maxCost > 50 ? '$45' : '$15',
      source: 'commercial'
    }
  ];

  // Filter by distance and budget
  let filteredCenters = centers.filter(center => {
    return center.distance <= constraints.transport.maxDistance && 
           center.estimatedCost <= constraints.budget.maxCost;
  });

  // Add adaptive scoring
  filteredCenters = filteredCenters.map(center => {
    let score = 100;

    // Distance scoring (closer is better)
    score += Math.max(0, 50 - (center.distance * 10));

    // Rating bonus
    score += (center.rating - 3.0) * 10;

    // Cost preference (free is better for budget-conscious)
    if (center.estimatedCost === 0) score += 20;

    // Waste type compatibility
    if (center.acceptedWaste.includes(wasteType)) score += 30;

    // Transport preference matching
    const transportPrefs = constraints.transport.preferences || [];
    transportPrefs.forEach(pref => {
      if (center.features.some(f => f.includes(pref.replace('_', '')))) {
        score += 15;
      }
    });

    return { ...center, adaptiveScore: Math.round(score) };
  });

  // Sort by adaptive score
  filteredCenters.sort((a, b) => b.adaptiveScore - a.adaptiveScore);

  return filteredCenters;
}

// Generate adaptive recommendations
function generateRecommendations(centers, wasteInfo, constraints) {
  const recommendations = [];

  if (centers.length === 0) {
    recommendations.push({
      type: 'no_results',
      message: `No suitable ${wasteType} disposal facilities found within ${constraints.searchRadius}km`,
      suggestions: [
        'Try increasing your search radius',
        'Consider alternative disposal methods',
        'Contact your local waste management authority'
      ]
    });
  } else {
    const topCenter = centers[0];
    recommendations.push({
      type: 'best_match',
      message: `${topCenter.name} is your best option`,
      score: topCenter.adaptiveScore,
      reasons: [
        `Only ${topCenter.distance}km away`,
        `Rated ${topCenter.rating}/5.0`,
        `Cost: ${topCenter.priceRange}`,
        `Accepts ${wasteType} waste`
      ]
    });

    // Budget-friendly options
    const freeOptions = centers.filter(c => c.estimatedCost === 0);
    if (freeOptions.length > 0) {
      recommendations.push({
        type: 'budget_friendly',
        message: `${freeOptions.length} free disposal option(s) available`,
        centers: freeOptions.slice(0, 2).map(c => c.name)
      });
    }
  }

  return recommendations;
}

// Main execution
const wasteInfo = categorizeWasteType(wasteType);
const transportConstraints = getTransportConstraints(transportMethod);
const budgetConstraints = getBudgetConstraints(budget);
const searchRadius = calculateAdaptiveRadius(radius, wasteInfo, urgency, environmentalFocus);

const constraints = {
  transport: transportConstraints,
  budget: budgetConstraints,
  searchRadius: searchRadius
};

const adaptiveCenters = generateAdaptiveCenters(latitude, longitude, wasteInfo, constraints);
const recommendations = generateRecommendations(adaptiveCenters, wasteInfo, constraints);

// Prepare response
const result = {
  success: true,
  timestamp: new Date().toISOString(),
  adaptive_analysis: {
    input_parameters: {
      location: { latitude, longitude },
      waste_type: wasteType,
      transport_method: transportMethod,
      accessibility: accessibility,
      budget_range: budget,
      urgency_level: urgency,
      environmental_focus: environmentalFocus
    },
    adaptations_applied: {
      search_radius_adjusted: `${radius}km → ${searchRadius}km`,
      waste_priority: wasteInfo.priority,
      transport_constraint: `${transportConstraints.maxDistance}km max`,
      budget_filter: budgetConstraints.label
    }
  },
  centers: adaptiveCenters,
  totalFound: adaptiveCenters.length,
  recommendations: recommendations,
  search_metadata: {
    original_radius: radius,
    adaptive_radius: searchRadius,
    filters_applied: [
      `Distance: ≤${transportConstraints.maxDistance}km`,
      `Budget: ${budgetConstraints.label}`,
      `Waste type: ${wasteType}`,
      `Priority: ${wasteInfo.priority}`
    ]
  }
};

return [{ json: result }];
